import React, { useState, useEffect } from 'react';
import axios from 'axios';
import NavBar from "./NavBar";
import {Link, useLocation} from "react-router-dom";
import {useForm} from "react-hook-form";

import * as events from "events";

function Details() {
  const [beer, setData] = useState([]);
  const [review, setReview] = useState("")
  let beerId = Number(useLocation().pathname.split('/').pop())


  useEffect(() => {
    axios.get('http://localhost:8000/details/' + beerId)
      .then(response => {
        setData(response.data);
      })
      .catch(error => {
        console.log(error);
      });
  }, []);



  const handleSumbit = (Event) => {
      Event.preventDefault();
      // alert(`the name you entered was ${name}`)
      axios.post('http://localhost:8000/review', beerId, review)
  }


  const {register,
        handleSubmit,
        formState: { errors }
    } = useForm({mode: "onSubmit", reValidateMode: "onBlur", resolver: yupResolver(validationSchema)});

  return (
      <div>
          <NavBar/>
          <h1>Beers</h1>
            {beer.map((b) =>
                <div key={b.id}>
                    <p> Name : {b.name} </p>
                    <p> Brewery : {b.brewery} </p>
                </div>
            )}
          <h2> Reviews</h2>
          <div>
              <form onSubmit={handleSumbit}>
                  <label> Enter your name:
                  <input type="text" value={review} onChange= {(e) => setReview(e.target.value)}/>
                  </label>
              </form>
          </div>
          <p> reviews : {review}</p>
          <p> beer id : {beerId}</p>
      </div>
  );
}

export default Details;
