import React, { useState, useEffect } from 'react';
import axios from 'axios';
import NavBar from "./NavBar";
import {Link, useLocation} from "react-router-dom";
import * as events from "events";

function Home() {
  const [beer, setData] = useState([]);
  const [review, setReview] = useState([])
  let beerId = Number(useLocation().pathname.split('/').pop())


  useEffect(() => {
    axios.get('http://localhost:8000/details/' + beerId)
      .then(response => {
        setData(response.data);
      })
      .catch(error => {
        console.log(error);
      });
  }, []);



  return (
      <div>
          <NavBar/>
          <h1>Beers</h1>
            {beer.map((b) =>
                <div key={b.id}>
                    <p> Name : {b.name} </p>
                    <p> Brewery : {b.brewery} </p>
                </div>
            )}
          <h2> Reviews</h2>
          <div>
              <form>
                  <textarea name={"review"} placeholder={"review"}/>
                  <input type={"submit"} onChange={(e) => setReview(e.target.value)}/>
              </form>
          </div>
          <p> reviews : {review}</p>
      </div>
  );
}

export default Home;
