import React, { useState, useEffect } from 'react';
import axios from 'axios';
import NavBar from "./NavBar";
import {Link, useLocation} from "react-router-dom";
import * as events from "events";

function Details() {
  const [beer, setData] = useState([]);
  const [review, setReview] = useState([])
  let beerId = Number(useLocation().pathname.split('/').pop())


  useEffect(() => {
    axios.get('http://localhost:8000/details/' + beerId)
      .then(response => {
        setData(response.data);
      })
      .catch(error => {
        console.log(error);
      });
  }, []);

  const handleChange = (Event) => {
      const review = Event.target.review;
      setReview(values => review)
  }

  const handleSumbit = (Event) => {
      Event.preventDefault();
      alert("hkeols")
  }


  return (
      <div>
          <NavBar/>
          <h1>Beers</h1>
            {beer.map((b) =>
                <div key={b.id}>
                    <p> Name : {b.name} </p>
                    <p> Brewery : {b.brewery} </p>
                </div>
            )}
          <h2> Reviews</h2>
          <div>
              <form onSubmit={handleSumbit}>
                  <input type="text" name={"review"} value="cool" onChange={handleChange}/>
              </form>
          </div>
          <p> reviews : {review.length}</p>
      </div>
  );
}

export default Details;
