import React, { useState, useEffect } from 'react';
import axios from 'axios';
import NavBar from "./NavBar";
import {Link, useLocation} from "react-router-dom";

function Home() {
  const [beer, setData] = useState([]);
  let beerId = Number(useLocation().pathname.split('/').pop())


  useEffect(() => {
    axios.get('http://localhost:8000/details/' + beerId)
      .then(response => {
        setData(response.data);
      })
      .catch(error => {
        console.log(error);
      });
  }, []);


  const onSubmit = async (formData) => {
      await axios.post('http://localhost:8000/reviews/' + beerId)
  }



  return (
      <div>
          <NavBar/>
          <h1>Beers</h1>
            {beer.map((b) =>
                <div key={b.id}>
                    <p> Name : {b.name} </p>
                    <p> Brewery : {b.brewery} </p>
                </div>
            )}
          <h2> Reviews</h2>
          <div>
              <form>
                  <textarea placeholder={"review"}/>
                  <input type={"submit"}/>
              </form>
          </div>
      </div>
  );
}

export default Home;
